mapper.select:
SELECT COUNT(*) > 0
FROM emp
WHERE emp_email = #{emp_email}

SELECT e.emp_name as emp_name, e.emp_email as emp_email, e.dept_num as dept_num, d.dept_name as dept_name, g.grade_name as grade_name
FROM emp e, dept d, grade g
WHERE e.dept_num = #{dept_num}
  AND e.dept_num = d.dept_num
  AND e.grade_num = g.grade_num
  LIMIT #{startRow}, #{size}
